ELSE IF (@CurrentSchema IS NULL OR @CurrentSchema != @ExistingSchema)
BEGIN
	SET @query = 'ALTER USER [' + @CurrentUser + '] WITH DEFAULT_SCHEMA = [' + @ExistingSchema + ']';
