ALTER PROCEDURE [Proc_CMS_Email_FetchEmailToSend]
	@FetchFailed bit,
	@FetchNew bit,
	@FirstEmailID int,
	@BatchSize bigint,
	@FailedEmailsNewerThan datetime2,
	@LastSendAttempt datetime2(7)
AS
BEGIN
SET NOCOUNT ON;

	IF @FetchFailed = 1 AND @FetchNew = 1
	BEGIN
		/* Get failed and waiting */
		;WITH q AS
		(
			SELECT TOP (@BatchSize) [EmailID],[EmailFrom],[EmailTo],[EmailCc],[EmailBcc],[EmailSubject],[EmailBody],[EmailPlainTextBody],
			[EmailFormat],[EmailPriority],[EmailSiteID],[EmailLastSendResult],[EmailLastSendAttempt],[EmailGUID],[EmailLastModified],
			[EmailStatus],[EmailIsMass],[EmailReplyTo],[EmailHeaders],[EmailCreated]
				FROM CMS_Email
				WHERE (EmailStatus = 1 AND EmailID > @FirstEmailID AND (EmailLastSendResult IS NULL OR @FailedEmailsNewerThan IS NULL OR EmailCreated > @FailedEmailsNewerThan))
				ORDER BY EmailPriority DESC, EmailID
		)
		UPDATE q SET EmailStatus = 2, EmailLastSendAttempt = @LastSendAttempt
			OUTPUT INSERTED.[EmailID],INSERTED.[EmailFrom],INSERTED.[EmailTo],INSERTED.[EmailCc],INSERTED.[EmailBcc],
				INSERTED.[EmailSubject],INSERTED.[EmailBody],INSERTED.[EmailPlainTextBody],INSERTED.[EmailFormat],
				INSERTED.[EmailPriority],INSERTED.[EmailSiteID],INSERTED.[EmailLastSendResult],INSERTED.[EmailLastSendAttempt],
				INSERTED.[EmailGUID],INSERTED.[EmailLastModified],INSERTED.[EmailStatus],INSERTED.[EmailIsMass],
				INSERTED.[EmailReplyTo],INSERTED.[EmailHeaders],INSERTED.[EmailCreated];
	END
	ELSE IF @FetchNew = 1
	BEGIN
		/* Get only waiting */	
		;WITH q AS
		(
			SELECT TOP (@BatchSize) [EmailID],[EmailFrom],[EmailTo],[EmailCc],[EmailBcc],[EmailSubject],[EmailBody],[EmailPlainTextBody],
			[EmailFormat],[EmailPriority],[EmailSiteID],[EmailLastSendResult],[EmailLastSendAttempt],[EmailGUID],[EmailLastModified],
			[EmailStatus],[EmailIsMass],[EmailReplyTo],[EmailHeaders],[EmailCreated]
				FROM CMS_Email
				WHERE (EmailStatus = 1 AND EmailID > @FirstEmailID AND EmailLastSendResult IS NULL)
				ORDER BY EmailPriority DESC, EmailID
		)
		UPDATE q SET EmailStatus = 2, EmailLastSendAttempt = @LastSendAttempt
			OUTPUT INSERTED.[EmailID],INSERTED.[EmailFrom],INSERTED.[EmailTo],INSERTED.[EmailCc],INSERTED.[EmailBcc],
				INSERTED.[EmailSubject],INSERTED.[EmailBody],INSERTED.[EmailPlainTextBody],INSERTED.[EmailFormat],
				INSERTED.[EmailPriority],INSERTED.[EmailSiteID],INSERTED.[EmailLastSendResult],INSERTED.[EmailLastSendAttempt],
				INSERTED.[EmailGUID],INSERTED.[EmailLastModified],INSERTED.[EmailStatus],INSERTED.[EmailIsMass],
				INSERTED.[EmailReplyTo],INSERTED.[EmailHeaders],INSERTED.[EmailCreated];
	END
	ELSE IF @FetchFailed = 1
	BEGIN
		/* Get only failed */
		;WITH q AS
		(
			SELECT TOP (@BatchSize) [EmailID],[EmailFrom],[EmailTo],[EmailCc],[EmailBcc],[EmailSubject],[EmailBody],[EmailPlainTextBody],
			[EmailFormat],[EmailPriority],[EmailSiteID],[EmailLastSendResult],[EmailLastSendAttempt],[EmailGUID],[EmailLastModified],
			[EmailStatus],[EmailIsMass],[EmailReplyTo],[EmailHeaders],[EmailCreated]
				FROM CMS_Email
				WHERE (EmailStatus = 1 AND EmailID > @FirstEmailID AND EmailLastSendResult IS NOT NULL AND (@FailedEmailsNewerThan IS NULL OR EmailCreated > @FailedEmailsNewerThan))
				ORDER BY EmailPriority DESC, EmailID
		)
		UPDATE q SET EmailStatus = 2, EmailLastSendAttempt = @LastSendAttempt
			OUTPUT INSERTED.[EmailID],INSERTED.[EmailFrom],INSERTED.[EmailTo],INSERTED.[EmailCc],INSERTED.[EmailBcc],
				INSERTED.[EmailSubject],INSERTED.[EmailBody],INSERTED.[EmailPlainTextBody],INSERTED.[EmailFormat],
				INSERTED.[EmailPriority],INSERTED.[EmailSiteID],INSERTED.[EmailLastSendResult],INSERTED.[EmailLastSendAttempt],
				INSERTED.[EmailGUID],INSERTED.[EmailLastModified],INSERTED.[EmailStatus],INSERTED.[EmailIsMass],
				INSERTED.[EmailReplyTo],INSERTED.[EmailHeaders],INSERTED.[EmailCreated];
	END
END



GO

ALTER PROCEDURE [Proc_CMS_Email_FetchMassEmail]
	@FetchFailed bit,
	@FetchNew bit,
	@EmailID int,
	@FirstUserID int,
	@BatchSize int,
	@LastSendAttempt datetime2(7)
AS
BEGIN
SET NOCOUNT ON;
	
	DECLARE @Users TABLE (UserID int, PRIMARY KEY(UserID))

	BEGIN TRANSACTION
	
	SET ROWCOUNT @BatchSize
	
	IF @FetchFailed = 1 AND @FetchNew = 1
		/* Get failed and waiting */
		INSERT INTO @Users 
			SELECT UserID FROM CMS_EmailUser 
				WHERE (EmailID = @EmailID AND UserID > @FirstUserID AND Status = 1) ORDER BY UserID;
		
	ELSE IF @FetchNew = 1		
		/* Get only waiting */
		INSERT INTO @Users 
			SELECT UserID FROM CMS_EmailUser 
				WHERE (EmailID = @EmailID AND UserID > @FirstUserID AND Status = 1 AND LastSendResult IS NULL) ORDER BY UserID;		
		
	ELSE IF @FetchFailed = 1
		/* Get only failed */
		INSERT INTO @Users 
			SELECT UserID FROM CMS_EmailUser 
				WHERE (EmailID = @EmailID AND UserID > @FirstUserID AND Status = 1 AND LastSendResult IS NOT NULL) ORDER BY UserID;
		
	ELSE
		/* Get archived */
		INSERT INTO @Users 
			SELECT UserID FROM CMS_EmailUser 
				WHERE (EmailID = @EmailID AND UserID > @FirstUserID AND Status = 3) ORDER BY UserID;	
	
	SET ROWCOUNT 0
	
	/* Status: 0 - created; 1 - waiting; 2 - sending; 3 - archived */
	UPDATE CMS_EmailUser SET Status = 2, LastSendAttempt = @LastSendAttempt WHERE (EmailID = @EmailID AND UserID IN (SELECT UserID FROM @Users));
	
	COMMIT TRANSACTION

	SELECT UserID, Email FROM CMS_User WHERE UserID IN (SELECT UserID FROM @Users);
END
GO

