CREATE PROCEDURE [Proc_CMS_PageUrlPath_CreateUrlPaths]
	@NodeID INT,
	@SiteID INT,
	@LastModified DATETIME,
	@UrlPaths Type_CMS_PageUrlPathsTable READONLY
AS
BEGIN
    BEGIN TRANSACTION
		BEGIN TRY
			INSERT INTO [CMS_PageUrlPath]([PageUrlPathLastModified], [PageUrlPathCulture], [PageUrlPathNodeID], [PageUrlPathUrlPath], [PageUrlPathUrlPathHash], [PageUrlPathSiteID], [PageUrlPathGUID])
			SELECT @LastModified, CultureCode, @NodeID, UrlPath, CONVERT(NVARCHAR(64), HASHBYTES('SHA2_256', LOWER(UrlPath)), 2) ,@SiteID, NEWID() 
			FROM @UrlPaths
		END TRY
		BEGIN CATCH
			IF XACT_STATE() = 1
			BEGIN
				DECLARE @PathsToInsert AS Type_CMS_PageUrlPathsTable
				DECLARE @NodeGUID AS NVARCHAR(36) = CONVERT(NVARCHAR(36), (SELECT TOP 1 [NodeGUID] FROM [CMS_Tree] WHERE [NodeID] = @NodeID))

				INSERT INTO @PathsToInsert 
				SELECT * FROM @UrlPaths

				UPDATE CollidingItem
					SET CollidingItem.UrlPath = CollidingItem.UrlPath + '-' + LOWER(CONVERT(VARCHAR(32), HASHBYTES('MD5', LOWER(CollidingItem.UrlPath + CollidingItem.CultureCode + @NodeGUID)), 2))
					FROM @PathsToInsert AS CollidingItem
					INNER JOIN [CMS_PageUrlPath] AS PageUrlPath 
						ON PageUrlPath.PageUrlPathUrlPathHash = CONVERT(VARCHAR(64), HASHBYTES('SHA2_256', LOWER(CollidingItem.UrlPath)), 2) AND PageUrlPath.PageUrlPathCulture =  CollidingItem.CultureCode
						WHERE PageUrlPath.PageUrlPathSiteID = @SiteID

				UPDATE CollidingItem
					SET CollidingItem.UrlPath = CollidingItem.UrlPath + '-' + LOWER(CONVERT(VARCHAR(32), HASHBYTES('MD5', LOWER(CollidingItem.UrlPath + CollidingItem.CultureCode + @NodeGUID)), 2))
					FROM @PathsToInsert AS CollidingItem
					INNER JOIN [CMS_AlternativeUrl] AS AlternativeUrl
						ON AlternativeUrl.AlternativeUrlUrl = CollidingItem.UrlPath
						WHERE AlternativeUrl.AlternativeUrlSiteID = @SiteID AND LEN(CollidingItem.UrlPath) <= 450

				INSERT INTO [CMS_PageUrlPath]([PageUrlPathLastModified], [PageUrlPathCulture], [PageUrlPathNodeID], [PageUrlPathUrlPath], [PageUrlPathUrlPathHash], [PageUrlPathSiteID], [PageUrlPathGUID])
				SELECT @LastModified, CultureCode, @NodeID, UrlPath, CONVERT(VARCHAR(64), HASHBYTES('SHA2_256', LOWER(UrlPath)), 2), @SiteID, NEWID() 
				FROM @PathsToInsert
			END
			ELSE
			BEGIN
				ROLLBACK TRANSACTION	
			END

		END CATCH	

		COMMIT TRANSACTION
END
GO

CREATE PROCEDURE [Proc_CMS_PageUrlPath_UpdateUrlPaths_Internal]
	@OriginalUrlPath NVARCHAR(2000),
	@NewUrlPath NVARCHAR(2000),
	@CultureCode NVARCHAR(50),
	@OriginalSiteID INT,
	@NewSiteID INT,
	@LastModified DATETIME,
	@StoreFormerUrls BIT
AS
BEGIN
	DECLARE @NewPaths TABLE(
		[PathID] INT NOT NULL, 
		[OriginalPath] NVARCHAR(2000) NOT NULL, 
		[OriginalPathHash] NVARCHAR(64) NOT NULL,
		[NewPath] NVARCHAR(2000) NOT NULL, 
		[NewPathHash] NVARCHAR(64) NOT NULL)
			
	INSERT INTO @NewPaths 
		SELECT 
			[PageUrlPathID], 
			[PageUrlPathUrlPath],
			[PageUrlPathUrlPathHash],
			@NewUrlPath, 
			CONVERT(VARCHAR(64), HASHBYTES('SHA2_256', LOWER(@NewUrlPath)), 2)
		FROM [CMS_PageUrlPath] 
		WHERE [PageUrlPathUrlPathHash] = CONVERT(VARCHAR(64), HASHBYTES('SHA2_256', LOWER(@OriginalUrlPath)), 2) AND [PageUrlPathCulture] = @CultureCode AND [PageUrlPathSiteID] = @OriginalSiteID
		UNION
		SELECT 
			[PageUrlPathID], 
			[PageUrlPathUrlPath],
			[PageUrlPathUrlPathHash],
			@NewUrlPath + SUBSTRING([PageUrlPathUrlPath], LEN(@OriginalUrlPath) + 1, LEN([PageUrlPathUrlPath])), 
			CONVERT(VARCHAR(64), HASHBYTES('SHA2_256', LOWER(@NewUrlPath + SUBSTRING([PageUrlPathUrlPath], LEN(@OriginalUrlPath) + 1, LEN([PageUrlPathUrlPath])))), 2)
		FROM [CMS_PageUrlPath] 
		WHERE [PageUrlPathUrlPath] LIKE @OriginalUrlPath + '/%' AND [PageUrlPathCulture] = @CultureCode AND [PageUrlPathSiteID] = @OriginalSiteID

	UPDATE [CMS_PageUrlPath]
	SET 
		[PageUrlPathUrlPath] = [NP].[NewPath],
		[PageUrlPathUrlPathHash] = [NP].[NewPathHash],
		[PageUrlPathLastModified] = @LastModified,
		[PageUrlPathSiteID] = @NewSiteID
	FROM @NewPaths as [NP]
	WHERE [CMS_PageUrlPath].[PageUrlPathID] = [NP].[PathID]

	IF (@StoreFormerUrls = 1)
	BEGIN
		DELETE FROM [CMS_PageFormerUrlPath]
		WHERE [PageFormerUrlPathCulture] = @CultureCode AND [PageFormerUrlPathSiteID] = @OriginalSiteID AND [PageFormerUrlPathUrlPathHash] IN (SELECT [OriginalPathHash] FROM @NewPaths)

		INSERT INTO [CMS_PageFormerUrlPath]
		SELECT 
			[NewPath].[OriginalPath] AS [PageFormerUrlPathUrlPath], 
			[NewPath].[OriginalPathHash] AS [PageFormerUrlPathUrlPathHash],
			[Original].[PageUrlPathCulture] AS [PageFormerUrlPathCulture],
			[Original].[PageUrlPathNodeID] AS [PageFormerUrlPathNodeID],
			@OriginalSiteID AS [PageFormerUrlPathSiteID],
			@LastModified AS [PageFormerUrlPathLastModified]
		FROM [CMS_PageUrlPath] as [Original]
		INNER JOIN @NewPaths as [NewPath] ON [Original].[PageUrlPathID] = [NewPath].[PathID]
	END	
END

GO

CREATE PROCEDURE [Proc_CMS_PageUrlPath_UpdateUrlPaths]
	@OriginalUrlPath NVARCHAR(2000),
	@NewUrlPath NVARCHAR(2000),
	@CultureCode NVARCHAR(50),
	@OriginalSiteID INT,
	@NewSiteID INT,
	@LastModified DATETIME,
	@ResolveConfilcts BIT,
	@StoreFormerUrls BIT
AS
BEGIN
    BEGIN TRANSACTION
		BEGIN TRY
			EXEC [Proc_CMS_PageUrlPath_UpdateUrlPaths_Internal]
				@OriginalUrlPath = @OriginalUrlPath,
				@NewUrlPath = @NewUrlPath,
				@CultureCode = @CultureCode,
				@OriginalSiteID = @OriginalSiteID,
				@NewSiteID = @NewSiteID,
				@LastModified = @LastModified,
				@StoreFormerUrls = @StoreFormerUrls
		END TRY
		BEGIN CATCH
			IF XACT_STATE() = 1
			BEGIN
				IF @ResolveConfilcts = 1
				BEGIN
					DECLARE @NodeID AS INT = (SELECT TOP 1 [PageUrlPathNodeID] FROM [CMS_PageUrlPath] 
						WHERE [PageUrlPathUrlPathHash] = CONVERT(VARCHAR(64), HASHBYTES('SHA2_256', LOWER(@OriginalUrlPath)), 2) AND [PageUrlPathCulture] = @CultureCode AND [PageUrlPathSiteID] = @OriginalSiteID)
					DECLARE @NodeGUID AS NVARCHAR(36) = CONVERT(NVARCHAR(36), (SELECT TOP 1 [NodeGUID] FROM [CMS_Tree] WHERE [NodeID] = @NodeID))

					SET @NewUrlPath = @NewUrlPath + '-' + LOWER(CONVERT(VARCHAR(32), HASHBYTES('MD5', LOWER(@NewUrlPath + @CultureCode + @NodeGUID)), 2))

					EXEC [Proc_CMS_PageUrlPath_UpdateUrlPaths_Internal]
						@OriginalUrlPath = @OriginalUrlPath,
						@NewUrlPath = @NewUrlPath,
						@CultureCode = @CultureCode,
						@OriginalSiteID = @OriginalSiteID,
						@NewSiteID = @NewSiteID,
						@LastModified = @LastModified,
						@StoreFormerUrls = @StoreFormerUrls
				END
				ELSE
				BEGIN
					DECLARE @NewValues TABLE(
					[PageName] NVARCHAR(100) NULL, 
					[CultureCode] NVARCHAR(50) NOT NULL, 
					[NodeID] INT NOT NULL, 
					[NewPath] NVARCHAR(2000) NOT NULL, 
					[NewPathHash] NVARCHAR(64) NOT NULL,
					[IsSelf] BIT NOT NULL,
					[Exists] BIT NOT NULL)

				INSERT INTO @NewValues 
					SELECT 
						[V].[DocumentName],
						[P].[PageUrlPathCulture],
						[P].[PageUrlPathNodeID],
						@NewUrlPath, 
						CONVERT(VARCHAR(64), HASHBYTES('SHA2_256', LOWER(@NewUrlPath)), 2),
						1,
						CASE WHEN [V].[DocumentName] IS NULL THEN 0 ELSE 1 END
					FROM [CMS_PageUrlPath] AS [P]
					LEFT JOIN [View_CMS_Tree_Joined] AS [V] WITH (NOLOCK) ON [PageUrlPathNodeID] = [V].[NodeID] AND [PageUrlPathCulture] = [V].[DocumentCulture] 
					WHERE [PageUrlPathUrlPathHash] = CONVERT(VARCHAR(64), HASHBYTES('SHA2_256', LOWER(@OriginalUrlPath)), 2) AND [PageUrlPathCulture] = @CultureCode AND [PageUrlPathSiteID] = @OriginalSiteID
					UNION
					SELECT 
						[V].[DocumentName],
						[P].[PageUrlPathCulture],
						[P].[PageUrlPathNodeID],
						@NewUrlPath + SUBSTRING([PageUrlPathUrlPath], LEN(@OriginalUrlPath) + 1, LEN([PageUrlPathUrlPath])), 
						CONVERT(VARCHAR(64), HASHBYTES('SHA2_256', LOWER(@NewUrlPath + SUBSTRING([PageUrlPathUrlPath], LEN(@OriginalUrlPath) + 1, LEN([PageUrlPathUrlPath])))), 2),
						0,
						CASE WHEN [V].[DocumentName] IS NULL THEN 0 ELSE 1 END
					FROM [CMS_PageUrlPath] AS [P]
					LEFT JOIN [View_CMS_Tree_Joined] AS [V] WITH (NOLOCK) ON [PageUrlPathNodeID] = [V].[NodeID] AND [PageUrlPathCulture] = [V].[DocumentCulture] 
					WHERE [PageUrlPathUrlPath] LIKE @OriginalUrlPath + '/%' AND [PageUrlPathCulture] = @CultureCode AND [PageUrlPathSiteID] = @OriginalSiteID

				DECLARE @Collisions TABLE(
					[Path] NVARCHAR(2000) NOT NULL, 
					[PageName] NVARCHAR(100) NULL, 
					[CultureCode] NVARCHAR(50) NOT NULL, 
					[NodeID] INT NOT NULL, 
					[IsAlternative] BIT NOT NULL,
					[CultureVersionExists] BIT NOT NULL,
					[SourcePageName] NVARCHAR(100) NULL, 
					[SourceCultureCode] NVARCHAR(50) NOT NULL, 
					[SourceNodeID] INT NOT NULL, 
					[SourceIsSelf] BIT NOT NULL,
					[SourceCultureVersionExists] BIT NOT NULL)

				INSERT INTO @Collisions
					SELECT 
						[P].[PageUrlPathUrlPath],
						[V].[DocumentName],
						[P].[PageUrlPathCulture], 
						[P].[PageUrlPathNodeID],
						0, 
						CASE WHEN [V].[DocumentName] IS NULL THEN 0 ELSE 1 END,
						[N].[PageName],
						[N].[CultureCode],
						[N].[NodeID],
						[N].[IsSelf],
						[N].[Exists]
					FROM [CMS_PageUrlPath] AS [P] 
					INNER JOIN @NewValues AS [N] ON [P].[PageUrlPathUrlPathHash] = [N].[NewPathHash] AND [P].[PageUrlPathSiteID] = @OriginalSiteID
					LEFT JOIN [View_CMS_Tree_Joined] AS [V] WITH (NOLOCK) ON [P].[PageUrlPathNodeID] = [V].[NodeID] AND [P].[PageUrlPathCulture] = [V].[DocumentCulture] 
					UNION
					SELECT 
						[A].[AlternativeUrlUrl], 
						[D].[DocumentName],
						[D].[DocumentCulture], 
						[D].[DocumentNodeID], 
						1, 
						CASE WHEN [D].[DocumentName] IS NULL THEN 0 ELSE 1 END,
						[N].[PageName],
						[N].[CultureCode],
						[N].[NodeID],
						[N].[IsSelf],
						[N].[Exists]
					FROM [CMS_AlternativeUrl] AS [A] 
					INNER JOIN @NewValues AS [N] ON [A].[AlternativeUrlUrl] = [N].[NewPath] AND [A].[AlternativeUrlSiteID] = @OriginalSiteID
					INNER JOIN [CMS_Document] AS [D] ON [A].[AlternativeUrlDocumentID] = [D].[DocumentID] 
			
				SELECT * FROM @Collisions
				END
			END
			ELSE
			BEGIN
				ROLLBACK TRANSACTION	
			END
		END CATCH	
	COMMIT TRANSACTION
END
GO

