	SELECT CAST(s.[StepActionParameters] AS XML) as xmlActionParameters FROM [CMS_WorkflowStep] s INNER JOIN [CMS_WorkflowAction] a 
	ON s.[StepActionID] = a.[ActionID]
	WHERE a.[ActionClass] = 'CMS.Newsletters.SendNewsletterIssueAction'
) AS T1

